package week1.day2;

public class OddNumbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i;
		for(i=1;i<=20;i++)
		{
			System.out.println(i);
			i=i+1;
		}
		
		//Condition - 2
		System.out.println("---------------------------------------------------------");
		int j;
		for(j=1;j<=20;j++)
		{
			if(j%2!=0)
			System.out.println(j);
		
		}
		
		
	}
	

}
